import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { CheckCircle, Mail } from 'lucide-react';

const categories = [
  {
    title: 'Melhores do Ano',
    candidates: [
      { id: '1', name: 'Samira França', avatar: '/avatars/a.jpg' },
      { id: '2', name: 'Isac Miranda', avatar: '/avatars/b.jpg' },

    ],
  },
  {
    title: 'Revelação do Ano',
    candidates: [
      { id: '3', name: 'Samira França', avatar: '/avatars/c.jpg' },
      { id: '4', name: 'Isac Miranda', avatar: '/avatars/d.jpg' },
    ],
  },
];

export default function VotingHomePage() {
  const [email, setEmail] = useState('');
  const [selected, setSelected] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const handleVote = () => {
    if (!email || !email.includes('@')) {
      alert('Por favor, insira um e-mail válido.');
      return;
    }
    console.log('Votando com e-mail:', email);
    console.log('Votos:', selected);
    setSubmitted(true);
  };

  const handleSelect = (category, candidateId) => {
    setSelected({ ...selected, [category]: candidateId });
  };

  if (submitted) {
    return (
      <div className="min-h-screen bg-black text-white flex flex-col items-center justify-center">
        <CheckCircle className="text-yellow-400 w-16 h-16 mb-4" />
        <h1 className="text-3xl font-bold">Voto Registrado com Sucesso!</h1>
        <p className="text-lg mt-2">Obrigado por participar da premiação.</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-zinc-900 to-black text-white p-6">
      <header className="text-center mb-10">
        <motion.h1
          className="text-4xl md:text-6xl font-extrabold text-yellow-400"
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Prêmio Melhores do Ano 2025-Cristal Acquacenter
        </motion.h1>
        <p className="text-zinc-300 mt-2 text-lg">Vote nos seus favoritos!</p>
      </header>

      <div className="max-w-4xl mx-auto space-y-12">
        {categories.map((cat) => (
          <div key={cat.title}>
            <h2 className="text-2xl font-bold text-yellow-300 mb-4">{cat.title}</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
              {cat.candidates.map((c) => (
                <button
                  key={c.id}
                  onClick={() => handleSelect(cat.title, c.id)}
className={`flex flex-col items-center p-4 rounded-xl border transition ${selected[cat.title] === c.id ? 'border-yellow-400 bg-yellow-900 bg-opacity-30' : 'border-zinc-700 hover:border-yellow-400'}`}
                >
                  <img
                    src={c.avatar}
                    alt={c.name}
                    className="w-24 h-24 rounded-full object-cover mb-3"
                  />
                  <span className="font-semibold text-white text-center">{c.name}</span>
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>

      <div className="mt-12 max-w-xl mx-auto">
        <label className="block text-zinc-300 mb-2 text-lg flex items-center gap-2">
          <Mail className="w-5 h-5" /> Seu E-mail:
        </label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="seuemail@exemplo.com"
          className="w-full px-4 py-2 rounded-lg bg-zinc-800 text-white border border-zinc-600 focus:border-yellow-400 focus:outline-none"
        />
        <button
          onClick={handleVote}
          className="mt-4 w-full bg-yellow-400 text-black font-bold py-2 rounded-lg hover:bg-yellow-300 transition"
        >
          Enviar Voto
        </button>
      </div>
    </div>
  );
}
